<?php

namespace Anytv\MainBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * NewsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class NewsRepository extends EntityRepository
{
    public function findAllNews($page, $items_per_page, $order_by, $order, $keyword)
    {
        $first_result = ($items_per_page * ($page-1));
                
        $query = $this->getEntityManager()->createQueryBuilder()
          ->select(array('n', 'nc'))
          ->from('Anytv\MainBundle\Entity\News', 'n')
          ->leftJoin('n.category', 'nc')
          ->where("n.title LIKE :keyword OR n.excerpt LIKE :keyword OR n.body LIKE :keyword")
          ->setParameter('keyword', "%$keyword%")
          ->setFirstResult($first_result)
          ->setMaxResults($items_per_page)
          ->orderBy('n.'.$order_by, $order)
          ->getQuery();
        
        return $query->getResult();
    }
    
    public function countAllNews($keyword)
    {    
        $query = $this->createQueryBuilder('n')
          ->select('count(n.id)')
          ->where("n.title LIKE :keyword OR n.excerpt LIKE :keyword OR n.body LIKE :keyword")
          ->setParameter('keyword', "%$keyword%")
          ->getQuery();
        
        return $query->getSingleScalarResult();
    }
}
